mapper.select:
SELECT * FROM BOARD_INFO

	
		AND BI_NUM=#{biNum}
	
	
		AND BI_TITLE=#{biTitle}
	
	
		AND BI_CONTENT=#{biContent}
	
	
		AND UI_NUM=#{uiNum}
	
	
		AND CREDAT=#{credat}
	
	
		AND CRETIM=#{cretim}
	
	
		AND LMODAT=#{lmodat}
	
	
		AND LMOTIM=#{lmotim}
	
	
		AND ACTIVE=#{active}

mapper.update:
UPDATE BOARD_INFO
SET BI_TITLE=#{biTitle}
,BI_CONTENT=#{biContent}
WHERE BI_NUM=#{biNum}
